mapper.update:
update project.tbluser set
pw = #{pw}, pname =
#{pname}, email = #{email}, birth = #{birth}
where id = #{id}

mapper.select:
  	select MAX(bno)
	from project.tblresume
	where userid = #{id}
	;

mapper.update:
	
	
    update project.tblresume 
	set title = #{title}, address = #{address}, postcode = #{postcode}, coverletter = #{coverletter}, jobstateid = #{jobstateid}, jobgroupid = #{jobgroupid}, jobgroupid2 = #{jobgroupid2}, rgbid = #{rgbid}, rgsid = #{rgsid}, employstatusid = #{employstatusid}, salaryid = #{salaryid}, img = #{img}
	where bno = #{bno}

  
	
	update tblresume R1
	set img = #{img}
	where bno = (select *
				from (select Max(bno)
						from tblresume R2) R3);

mapper.select:
 	
	select t.*
	from project.tbltel t
	where t.rid=#{bno}

mapper.update:

